, '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('114', '3', '17', '0', '0', '0', '0', '0', '兴业银行深圳分行', '', '', '', '', '', '', '<h1 style=\"text-align:left;margin:0px;padding:0px;list-style:none;width:706px;height:31px;line-height:31px;font-weight:normal;font-size:14px;background-image:none;background-position:initial;background-size:initial;background-repeat:initial;background-attachment:initial;background-origin:initial;background-clip:initial;border-bottom:1px solid #F0F0F0;position:relative;z-index:1;overflow:visible;font-family:宋体;white-space:normal;\">\r\n	兴业银行深圳分行诚聘英才-寻千里马\r\n</h1>\r\n<h2 style=\"text-align:left;margin:0px;padding:0px;list-style:none;width:706px;height:31px;line-height:31px;font-weight:normal;font-size:12px;background-image:none;background-position:initial;background-size:initial;background-repeat:initial;background-attachment:initial;background-origin:initial;background-clip:initial;border-bottom:1px solid #F0F0F0;color:#333333;text-indent:10px;font-family:宋体;white-space:normal;\">\r\n	（工作地点：深圳\\r\n</h2>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	兴业银行股份有限公司（股票代\:601166）成立于1988年，是中国首家赤道银行，是中国股份制商业银行中唯一一家以“银行”为主体、拥\9大牌照（信托、金融租赁、基金、消费金融、期货、资产管理、互联网金融、研究咨询、数字金融）的现代综合金融服务集团。截\2016年末，总资产\6万亿元，居股份制同类银行首位、全球上市企\100强、全球银\50强；拥有126家分行（香港首家境外分行）\2003家分支机构、全\1500多家代理行，员工数\5万\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	兴业银行深圳分行\1998年开业，在社会各界和广大客户的鼎力支持下，各项业务持续快速健康发展，截至2016年末，总资\2826亿元，拥\45家分支机构，员工数近1300人，存贷款总量及利润排名俱佳，属兴业银行分行第一梯队，列深圳市股份制商业银行第一梯队，为优秀人才提供了广阔的事业发展空间。因业务发展需要，热忱欢迎有志于金融行业、勇于接受挑战、德才兼备、渴望成功的人才加入我们的行列，并肩搏击兴业发展新纪元！\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	\<strong style=\"margin:0px;padding:0px;list-style:none;\">综合支行行长</strong> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	本科以上学历\8年以上经济金融从业经历（在金融同业同级职位任职或下一职位任职3年以上）；具有较强的管理能力、协调能力和市场拓展能力；有能力组建业务团队并带领团队完成各项经营指标\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	\<strong style=\"margin:0px;padding:0px;list-style:none;\">企业金融业务部总监</strong><strong style=\"margin:0px;padding:0px;list-style:none;\"><span style=\"margin:0px;padding:0px;list-style:none;color:#007FFF;\">（支行行长级以上待遇\</span></strong><strong style=\"margin:0px;padding:0px;list-style:none;\"></strong> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	本科以上学历\8年以上经济金融从业经历；具有较强的管理能力、协调能力和市场拓展能力；有能力组建业务\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	\<strong style=\"margin:0px;padding:0px;list-style:none;\">分行零售金融部门副总经理（个贷或财富方向）</strong> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	本科以上学历\5年以上经济金融从业经历（在金融同业同级职位任职或下一职位任职3年以上）；具有较强的零售金融经营管理能力，个贷或财富业务专长突出，可依据深圳地区特色达成本行专项业绩指标，有能力协助部门领导完成本行个贷或财富业务的营销推动、组织协调、业务管理等工作目标。具有银行对公或对私业务负责人经验者优先考虑\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	\<strong style=\"margin:0px;padding:0px;list-style:none;\">综合支行副行长或行长助理（分管零售）</strong> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	本科及以上学历，5年以上经济金融从业经历（在金融同业同级职位任职或下一职位任职2年以上），具有丰富的零售团队管理和市场拓展经验，熟悉银行零售业务产品和深圳市场情况；有能力协助支行长带领员工完成各项零售经营指标\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	\<strong style=\"margin:0px;padding:0px;list-style:none;\">企业金融客户经理</strong> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	本科及以上学历，2年以上经济金融从业经历，具有丰富的客户资源、较强的拓展能力和良好的社会关系，熟悉深圳金融市场，深入了解客户需求。具有特殊客户资源的，可适当放宽条件\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	\<strong style=\"margin:0px;padding:0px;list-style:none;\">零售客户经理</strong> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	本科及以上学历，2年以上经济金融从业经历，具有较强的营销能力，勤奋肯干，具有特殊或成熟零售渠道者优先考虑，并可适当放宽条件\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	\<strong style=\"margin:0px;padding:0px;list-style:none;\">柜员</strong> \r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	本科以上学历\26周岁以下\2年以上银行支付结算业务从业经历，业务技能娴熟，形象气质佳，意愿关外区域工作者优先考虑\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\">\r\n	<span style=\"color:#2F2F2F;font-family:宋体;font-size:15px;white-space:normal;background-color:#FFFFFF;\">如有意愿，请前往\</span><span font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\" style=\"margin: 0px; border: 0px; padding: 0px; font-family: 宋体; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(53, 53, 53);\"><a href=\"http://44e.zbj99.cn/login.php\" style=\"margin:0px;border:0px;padding:0px;text-decoration:none;color:#333333;transition:all 0.3s ease-in-out;\">http://www.longmenchina.com/main.php</a></span> \r\n</p>', '', '', '2018011711160251.jpg', '', '', '', '', '', '', '', '', '', '诚聘英才-寻千里马', '诚聘英才-寻千里马', '12', '0', '0', '1', '0', '1515291176', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('115', '3', '17', '0', '0', '0', '0', '0', '国务院国资委新闻中心', '', '', '', '', '', '', '<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	国务院国有资产监督管理委员会新闻中心是国资委直属事业单位，主要承担国资委和中央企业的新闻宣传工作。根据工作需要，国资委新闻中心面向社会公开招聘工作人员。现将有关事项公告如下：\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	一、报名条\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	（一）遵守中华人民共和国宪法和法律，具有良好的品行和职业道德\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	（二）具有招聘职位所需的专业背景和工作技能\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	（三）具有正常履行岗位职责的身体条件\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	二、招聘程\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	（一）应聘方\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	招聘职位相关信息及任职资格要求详见《国务院国资委新闻中心公开招聘岗位信息表》（附件1）。请将个人简历和《国务院国资委新闻中心公开招聘报名登记表》（附件2）发送至xwzx@sasac.gov.cn，邮件及附件标题按“应聘职位代码—姓名”格式注明。应聘人员邮件中还需提交以下资料：①学历、学位相关证明；②职（执）业资格、专业技术职务资格证书或专业能力证明；③主要成果作品等。上述材料均不应涉及国家秘密\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	（二）招聘程\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	将通过简历筛选、面试、笔试、专业能力测试、体检和背景调查等程序，择优确定录用人选\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	三、其\\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;\" align=\"left\">\r\n	<br />\r\n</p>\r\n<p style=\"margin-top:0px;margin-bottom:0px;padding:0px;list-style:none;font-family:宋体;white-space:normal;text-align:justify;text-indent:2em;font-size:16px;\">\r\n	每位应聘人限\1个职位。报名时，提交的申请材料应当真实、准确。应聘人提供虚假申请材料，一经查实，即取消应聘资格。对伪造、变造有关证件、材料、信息，骗取考试资格的，将按照有关规定移交相关部门处理\\r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n<p>\r\n	<span style=\"color:#2F2F2F;font-family:宋体;font-size:15px;white-space:normal;background-color:#FFFFFF;\">如有意愿，请前往\</span><span font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\" style=\"margin: 0px; border: 0px; padding: 0px; font-family: 宋体; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(53, 53, 53);\"><a href=\"http://44e.zbj99.cn/login.php\" style=\"margin:0px;border:0px;padding:0px;text-decoration:none;color:#333333;transition:all 0.3s ease-in-out;\">http://www.longmenchina.com/main.php</a></span> \r\n</p>', '', '', '2018011711170286.jpg', '', '', '', '', '', '', '', '', '', '2018年第一批公开招聘公告', '2018年第一批公开招聘公告', '8', '0', '0', '1', '0', '1515291176', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('121', '3', '18', '0', '0', '0', '0', '0', '深圳市大鹏新区大鹏办事处', '', '', '', '', '', '', '<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	根据《深圳市事业单位常设岗位工作人员招聘办法\(深人社规\2012\16\)有关规定，经大鹏新区组织人事局核准，我办事处决定选聘1名十三级专业技术岗位职员至财务管理中心，具体岗位参见《大鹏新区大鹏办事处选聘职员职位表\(见附\1，以下简称《职位表\),现就有关事项公告如下\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	一、选聘要求及条\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(一)选聘范围及条\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	1.符合列入深圳市驻深部队随军家属安置计划的职员\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	2.符合本公告及《职位表\(见附\1)规定的资格条件\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(\)有关要求\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	1.报考专\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(1)应聘人所学专业须与《职位表》要求一\(专业代码相符)。专业是否相符可参考国家教育部颁布的《普通高等学校高职高专教育指导性专业目\(试行)教高[2004]3号》\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(2)所学专业须与职位规定的学历层次相对应\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	2.报考年龄：45周岁以下(\1972\1\1日之后出\)\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	3.户籍要求：市内外户籍\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(\)下列人员不得应聘\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	1.尚未解除党纪、政纪处分或正在接受纪律审查的人员以及涉嫌违法犯罪正在接受司法审查尚未做出结论的人员;\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	2.2013\1月以来参加本市机关事业单位招考被认定有考试作弊行为的人\; 3.其他属法律法规规定不能应聘的\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	二、选聘程序\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(一)发布公告\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	在大鹏新区政府在线网站发布选聘公告\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(\)报名和资格初\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	1.报名和资格初审：应聘人员凭报名表、身份证、学历学位证、随军家属配偶证明材料、市外机关事业单位在编在岗证明、计划生育材料、现实政治表现、无违法犯罪证明等材料报名及资格初审\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	<span style=\"margin:0px auto;padding:0px;list-style:none;color:#FF0000;\">2.报名时间:2018\1\19\(星期\)，上\9:30-12:00，下\14:30-17:30</span> \r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	3.报名地点：深圳市大鹏新区大鹏办事处中山路5\1318号办公室，联系人：陈女士;联系电话\84301657\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	4.资格初审\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	资格初审与报名同步进行。根据应聘人员提供的资料和资格审查结果，由大鹏新区大鹏办事处组织审查应聘人员的资格条件，对符合条件的人员现场发放面试通知书\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(\)面试\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	委托专业面试机构进行面试。面试时间和地点另行通知。面试总成\100分，60分以上视为合格并有排名资格。大鹏办事处在面试结束后5个工作日内在深圳市大鹏新区政府在线公布面试结果，并按照录用名\1:1的比例确定体检人选\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(\)体检\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	体检人员携带本人身份证及近期一寸正面免冠彩色相\1张，按时参加体检。体检时间及体检医院另行通知。不按时参加体检者，视为自动放弃。体检标准参照《广东省事业单位公开招聘人员体检实施细则(试行)》执行\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(\)组织考察\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	体检合格的人员，由考察组通过实地走访、查阅人事档案等方式，全面了解考生德、能、勤、绩、廉情况，工作实绩、履职能力以及与岗位的匹配程度，对考察人选进行考察，考察合格的确定为拟聘人员\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	(\)办理手续\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	确定的拟选聘人选由大鹏党工委组织部办理正式入职手续，并在发布公告的网站上公布结果。选聘人员办理聘用手续时，实行半年试用期，并聘任在十三级专业技术岗位\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	三、其\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	本公告未尽事宜按《深圳市事业单位常设岗位工作人员招聘办法\(深人社规[2012]16\)有关规定执行\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	本公告由大鹏新区大鹏办事处党工委组织部负责解释\\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	<span style=\"color:#2F2F2F;font-family:宋体;font-size:15px;white-space:normal;background-color:#FFFFFF;\">如有意愿，请前往\</span><span font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\" style=\"margin: 0px; border: 0px; padding: 0px; font-family: 宋体; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(53, 53, 53);\"><a href=\"http://44e.zbj99.cn/login.php\" style=\"margin:0px;border:0px;padding:0px;text-decoration:none;color:#333333;transition:all 0.3s ease-in-out;\">http://www.longmenchina.com/main.php</a></span> \r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-family:宋体;font-size:14px;margin:0px auto;padding:0px;list-style:none;text-indent:2em;color:#444444;white-space:normal;background-color:#FFFFFF;text-align:right;\">\r\n	<br />\r\n</p>', '', '', '2018011711473923.png', '', '', '', '', '', '', '', '', '', '广东深圳市大鹏新区大鹏办事处公告', '2018公开选聘事业编制职员1\', '12', '0', '0', '1', '0', '1515291176', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('296', '1', '9', '0', '0', '0', '0', '0', '1', '', '', '', '', '', '', '', '', '', '2018020216383968.jpg', '', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '1', '1', '1517560706', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('122', '3', '18', '0', '0', '0', '0', '0', '深圳市龙岗区中心血\', '', '', '', '', '', '', '<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">根据工作需要，经研究，深圳市龙岗区中心血站现面向社会公开招聘聘员1名，具体岗位参见《深圳市龙岗区中心血站公开招聘聘员岗位表\(见附\1，以下简称《岗位表\)，有关事项公告如下：\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　一、招聘对\</strong> \r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　符合本公告及报考岗位所要求条件的人员均可报考\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　二、招聘条\</strong> \r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(一)具有中华人民共和国国\;\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)遵守我国宪法和法\;\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)具有良好的品行和职业道德;\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)具备岗位所需的专业和技能条\;\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)具备适应岗位要求的身体条\;\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)符合本公告及《岗位表》所规定的其他条件\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　有下列情况者，不予接受报考：\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(一)受过党纪、政纪处分以及正在接受有关部门审查尚未作出结论的人员;\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)违反计划生育政策人员;\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)法律法规规定的其他情形\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　三、报名程\</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(一)报名及资格初\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　报名及资格初审时间：公告发布之日起至2018\1\22日，上午8:30\12:00，下\2:30\5:00(含当日，仅限工作\ ，双休日及国家法定节假日不接受报\)\</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　报名及资格初审地点：龙岗区中心城和谐路卫生监督大楼龙岗区中心血\411室\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　报名方式：现场报名\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　报名咨询电话\0755-28989581\0755-89551948.\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)考生报名接受资格初审应提供以下材料：\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1.应聘简\;\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2.身份证复印件(验原\);\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　3.学历学位证书及相关验证证明复印件(验原\);\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　4.《深圳市龙岗区中心血站公开招聘聘员报名表\(一式两份，照片请提前张贴好)(附件2);\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　5.《深圳市龙岗区中心血站招聘聘员计划生育情况个人承诺书\(附件3);\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　6.近期一寸正面免冠彩色照\2\(若在报名表已经张贴照片，则不再提供彩色照\);\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　7.公安部门出具的《无违法犯罪记录证明\;\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　8.《岗位表》所规定的其他材\(报名时必须提供复印件，验原件)\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)注意事项\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1.报名者须本人到场，不可委托他人代报名\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2.岗位凡是有工作经历要求的，报名者须提供现工作单位出具的工作证明或劳动合同或社保清单。如果报名当天不能提供的，须现场签订承诺书，在资格复审时提供\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　3.学历学位验证证明、《无违法犯罪记录证明》、工作经验证明等因客观原因暂无法提供的，可以先签署承诺书，在资格复审时提供\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　4.本公告所有时间计算截点为2017\12\31日\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　五、考核方式</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1.简历筛\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　龙岗区中心血站将对应聘者简历进\1\5的比例进行初步筛选，筛选通过人员将以电话方式通知符合条件的人员参加下一轮考核\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2.面试\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　考试采取面试和操作考核方式，依据成绩高低排序，等额确定资格复审和体检人选，面试时间另行通知\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　六、体检</strong> \r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(一)范围：按面试成绩高低进行等额体检;\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)体检时间和地点另行通知(体检费用自理)\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　七、资格复审及聘用</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　考试及体检合格的拟聘人员进行资格复查，体检或报考资格复查不合格者，不予聘用，所空出的职位按该职位面试成绩排名先后依次审核递补。招聘结果将在龙岗区卫计局网站公示5个工作日，经公示无异议后，按有关规定办理聘用手续\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　八、用工模式和待遇</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(一)岗位设置试用期，试用\3个月，试用期包含在劳动合同期限内。若试用期合格，则继续聘\;若试用期不合格，用人单位可以解除劳动合同;在聘用的第一年须通过国家卫计委规定的血站上岗证考试，有两次考试机会，若均未通过，用人单位可以解除劳动合同\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)正式录用后，与用人单位签订劳动合同\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)工资福利待遇按龙岗区有关规定执行\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　本公告由龙岗区中心血站负责解释\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　<span style=\"color:#2F2F2F;font-family:宋体;font-size:15px;white-space:normal;background-color:#FFFFFF;\">如有意愿，请前往\</span><span font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\" style=\"margin: 0px; border: 0px; padding: 0px; font-family: 宋体; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(53, 53, 53);\"><a href=\"http://44e.zbj99.cn/login.php\" style=\"margin:0px;border:0px;padding:0px;text-decoration:none;color:#333333;transition:all 0.3s ease-in-out;\">http://www.longmenchina.com/main.php</a></span> \r\n	</p>', '', '', '2018011711460872.jpg', '', '', '', '', '', '', '', '', '', '2018\1月深圳市龙岗区中心血站招聘聘员公\', '2018\1月招聘聘员公\', '8', '0', '0', '1', '0', '1515291176', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('294', '1', '9', '0', '0', '0', '0', '0', '1', '', '', '', '', '', '', '', '', '', '2018020216371029.png', '', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '1', '5', '1517560619', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('295', '1', '9', '0', '0', '0', '0', '0', '1', '', '', '', '', '', '', '', '', '', '2018020216372539.png', '', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '1', '0', '1517560634', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('123', '3', '18', '0', '0', '0', '0', '0', '广州市中医医\', '', '', '', '', '', '', '<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">广州市中医医院是广州市卫生和计划生育委员会直属事业单位，现有2个院区\2个门诊部，分别为珠玑路总院、同德围分院、同德门诊部、五羊门诊部，核定病床数\722张\4年内，我院还将在<a href=\"http://www.shiyebian.net/guangdong/tianhequ/\" target=\"_blank\" class=\"infotextkey\" style=\"color:#003399;text-decoration:none;\">天河\</a>建成一\800床的新院区。届时医院病床数将超\1500张\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　我院珠玑路总院坐落于有“西关”之称的<a href=\"http://www.shiyebian.net/guangdong/liwanqu/\" target=\"_blank\" class=\"infotextkey\" style=\"color:#003399;text-decoration:none;\">荔湾\</a>黄沙地段，与风景秀丽的沙面、白鹅潭相邻; 同德围分院位于同德街西槎路泽德花园二期的东南角、本院同德门诊部旁，是同德围地区首家大型综合性医院，服务区域内有居民10多万，是广州市医疗卫生设施布局重点项目\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　我院是一所集医疗、科研、教学、预防保健于一体的综合性三级甲等中医医院，是广州中医药大学非直属附属医院、广东省中医名院建设单位、广州市中医名院，广州市中医中药研究所设于院内。医院下设同德门诊部和五羊门诊部两个分支机构。曾荣获“省文明中医医院”、“省百家文明医院”等称号;拥有博士、硕士研究生导师51名，独立招收、培养中医、中西医结合的博士、硕士研究生，承担广州中医药大学学生实习、带教任\;医院学科齐全，重点专科优势明显，设专业科\20多个，专\(专病)80多个;有国家级中医重点专科4个，省市级中医重点专\(专病)、中医名\20余个，其中，脉管炎科是全国最早建立的两个脉管炎专科之一，早\1978年就获得全国科学大会奖。医院承担各级科研课题近百项，多项课题获国家、省、市科研成果奖\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　现因业务发展需要，向社会公开招聘人才\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　一、拟招聘的专业、人数及应聘人员须具备的资格条件\</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(一)基本条件\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1、遵守中华人民共和国宪法和法律\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2、具有良好的品行、职业道德和团队协作精神\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　3、具有岗位所需的专业技术资格条件、任职资格、职\(执业)资格及技能要求\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　4、适应岗位要求的身体、心理条件\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　5、具备岗位所需要的其他条件\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)资格条件\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1、具有正高级专业技术资格的，年龄要求在50周岁以下\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2、具有博士学位或副高级专业技术资格的，年龄要求在45周岁以下\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　3、具有研究生学历或硕士学位，或广州市常住户籍人员具有中级专业技术资格的，年龄要求在40周岁以下\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　4、具有普通高等教育本科学历并有学士学位，或具有教育部认证的国(\)外学士学位，或具有本科学历且具有相关专业中级专业技术资格或执业资格的，或广州市常住户籍人员具有本科学历的，年龄要求\35周岁以下\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　上述学历学位在国(\)外取得的，要求同时出具教育部认证材料\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　年龄计算截止时间\2017\12\31日\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　5、应届毕业生大学英语四级考试成绩\425分或以上\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)招聘岗位、专业、学历、人数及资格条件等：(见附\)\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　备注：本次公告招聘岗位为非事业编制人员岗\;2018年毕业人员不符合本次招聘需求\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　二、应聘程\</strong> \r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(一)报名\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　1、报名时间：即日起至2018\1\25\</strong> \r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2、报名方式：\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　在本公告附件1中下载《广州市中医医院2018年第一批公开招聘人员报名\(报考岗位及代码，考生姓名，所学专业，手机号码)》，于报名时间内将表格以附件形式发送至邮箱gkzpci@126.com报名\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　邮件填写方式举例\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"><br />\r\n</p>\r\n<center style=\"font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <img alt=\"\" src=\"http://www.yixue99.com/uploadfile/2018/0117/20180117095839348.png\" width=\"473\" height=\"208\" style=\"border-width:initial;border-style:none;\" /> \r\n	</center>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"><br />\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)考试\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　报名并经我院确认后，符合招聘条件人员参加我院组织的招聘考试\\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1、考试地点、范围：\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　考试在广州市中医医院珠玑路总院进行(地址：广州市荔湾区珠玑路16\)\\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　程序包括笔试、实践能力考试及面试，内容包括本专业相关知识、实际工作能力以及综合素质。通过考试测试应聘者和岗位相适应的专业知识、实际工作能力以及发展潜力\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2、考试时间：报名结束后5个工作日内，在广州市中医医院网站(http://www.gzszyy.com/)“人才招聘”中查询\\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　3、具体程序：\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(1)考生按报考专业的时间安排参加笔试。笔试结束后，成绩在广州市中医医院网\(http://www.gzszyy.com/)“人才招聘”中发布\\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(2)笔试成绩从高到低排序，按1\5的比例确定参加实践能力考试的人\(参加笔试人员比例达不\1\5，则按实际参加人数进行实践能力考核)。考生放弃参加实践能力考核，可按笔试成绩高低顺序依次替补人选\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(3)完成实践能力考核的人员可参加面试\\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(4)面试完毕后当场评分、统分，并向考生宣布面试成绩。面试采取百分制\60分及以上为合格，成绩合格者才能进入体检、考察环节\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(5)按面试成绩从高到低排名，\1:1的比例确定参加体检人选\\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)体检\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　体检参照市委组织部、市财政局、市人社局、市卫生局《关于印发广东省事业单位公开招聘人员体检实施细则(试行)的通知\(穗人社发\2011\52\)的精神执行，其中，放射工作人员还需按《放射工作人员职业健康管理办法》和《放射工作人员健康标准》执行，体检工作由我院自行安排\\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)考察\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　对体检合格的应聘人员进行考察，主要包括：思想政治、道德品质、能力素质、遵纪守法情况、工作或学习表现情况、应聘资格条件等\\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　考生体检或考察不合格的，视为不符合我院招聘条件，我单位可按考试成绩高低顺序依次替补人选\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)确定拟聘人员，公示：\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　通过考试、考察，体检合格人员，将与我院签订劳动合同，确定劳动关系\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　三、参加考试时需要提交的相关材料</strong> \r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1、应届毕业生：身份证、英语等级证书\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2、在职人员：身份证、毕业证、学位证、资格证、执业证等\\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　四、其他需要说明事\</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1、凡受到党纪处分期限未满或者正在接受纪律审查的人员，处于刑事处罚期间或者正在接受司法调查尚未做出结论的人员，不接受其报名申\;\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2、应聘人员提供的身份证明和岗位所需的资格材料应当真实有效，如发现弄虚作假，取消其应聘资格\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　3、应聘人员违反国家、省、市计划生育有关规定，未处理或处理期未满五年的，取消应聘资格，或随时解除劳动合同\\r\n		</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"><br />\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"><span style=\"color:#2F2F2F;font-family:宋体;font-size:15px;white-space:normal;background-color:#FFFFFF;\">如有意愿，请前往\</span><span font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\" style=\"margin: 0px; border: 0px; padding: 0px; font-family: 宋体; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(53, 53, 53);\"><a href=\"http://44e.zbj99.cn/login.php\" style=\"margin:0px;border:0px;padding:0px;text-decoration:none;color:#333333;transition:all 0.3s ease-in-out;\">http://www.longmenchina.com/main.php</a></span> \r\n		</p>', '', '', '2018011711300921.jpg', '', '', '', '', '', '', '', '', '', '招聘公告', '2018年招聘公\(第一\)', '14', '0', '0', '1', '0', '1515291176', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('293', '1', '9', '0', '0', '0', '0', '0', '1', '', '', '', '', '', '', '', '', '', '2018020216365534.jpg', '', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '1', '2', '1517560365', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('124', '3', '18', '0', '0', '0', '0', '0', '上海交通大学附属胸科医\', '', '', '', '', '', '', '<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">学科介绍：上海市肺部肿瘤临床医学中心(上海市胸科医院肿瘤科)在国内率先建立肺癌多学科诊治团队，开展精准医学指导下的个体化综合治疗。科室承担国家重点研发计划、科技部国家国际合作专项，国家自然科学基金7项，省部级以上课\31项。承\40余项国际多中心临床试验，其中包括三项国家1.1类新药临床研究\2009年，肿瘤科成功完成中国大陆第一例达芬奇机器人辅助下的肺癌肺叶切除术、胸腺瘤切除术等。目前肿瘤科达芬奇手术保持全国第一。施行了国内第一例四维磁导航引导下的肺癌经皮氩氦刀消融术，多次实施了射频消融治疗、氩氦冷冻消融微创治疗等\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　导师团队：陆舜教\/博导，陈智伟教授/博导、罗清泉教授/博导\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　研究方向:\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1. 肺癌多学科诊治、精准治疗及肺癌免疫治疗\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2, 肺癌诊治的新兴前沿技术，包括基于多组学的肺癌诊治、肺癌微创诊治、人工智能应用、大数据信息挖掘使用、新药研发等\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　联系方式\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　<span style=\"color:#2F2F2F;font-family:宋体;font-size:15px;white-space:normal;background-color:#FFFFFF;\">如有意愿，请前往\</span><span font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\" style=\"margin: 0px; border: 0px; padding: 0px; font-family: 宋体; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(53, 53, 53);\"><a href=\"http://44e.zbj99.cn/login.php\" style=\"margin:0px;border:0px;padding:0px;text-decoration:none;color:#333333;transition:all 0.3s ease-in-out;\">http://www.longmenchina.com/main.php</a></span> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"><a href=\"http://www.shiyebian.net/shanghai/xuhuiqu/\" target=\"_blank\" class=\"infotextkey\" style=\"color:#003399;text-decoration:none;\"></a> \r\n</p>', '', '', '2018011711333118.jpg', '', '', '', '', '', '', '', '', '', '2018上海交通大学附属胸科医院博士后招聘启事', '博士后招聘启\', '4', '0', '0', '1', '0', '1515291176', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('125', '3', '18', '0', '0', '0', '0', '0', '中科院地理资源所土地科学与生物地理研究室', '', '', '', '', '', '', '<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">根据研究工作需要，中国科学院地理科学与资源研究所土地科学与生物地理研究室张镱锂研究团队招聘博士后2-3名。欢迎对下述研究主题感兴趣的青年学者报名\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　一、研究方\</strong> \r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1. 高原土地利用与土地覆被调查与制图\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2. 土地覆被变化遥感探测技\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　3. 土地覆被变化过程与机理研\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　4. 数据空间化技术方\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　5. 高原土地利用变化的土壤环境效\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　6. 农业土地资源评价与开发潜力研\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　二、申请条\</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　能够适应高原工作环境，年龄一般在35岁以\;\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　已获得博士学位或应届博士研究\;\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　具有相关专业背景和研究经\;\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　英语交流能力良好;\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　具有青藏高原研究基础或山地工作经验者优先\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　合作导师：张镱锂 研究\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　三、工作待\</strong> \r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　在站期间，享受中科院地理资源所博士后工资和生活待遇,具体面议\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　工作期限原则上为两年，根据工作需要可适当延长\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　四、申请程序及提交材料</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　申请者须通过电子邮件或信件，提交以下材料\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(1)申请书与个人简\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(2)博士学位证书的复印件(暂未取得学位证书的应届生须提交所在单位证明，说明证书授予时间)\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(3)推荐\2\(包括博士学位导师推荐\1\)\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(4)科研成果目录、博士学位论文摘要，并提\2篇代表性学术论文的全文复印件或PDF文件\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　五、联系方\</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　<span style=\"color:#2F2F2F;font-family:宋体;font-size:15px;white-space:normal;background-color:#FFFFFF;\">如有意愿，请前往\</span><span font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\" style=\"margin: 0px; border: 0px; padding: 0px; font-family: 宋体; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(53, 53, 53);\"><a href=\"http://44e.zbj99.cn/login.php\" style=\"margin:0px;border:0px;padding:0px;text-decoration:none;color:#333333;transition:all 0.3s ease-in-out;\">http://www.longmenchina.com/main.php</a></span> \r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"><br />\r\n	</p>', '', '', '2018011711353976.jpg', '', '', '', '', '', '', '', '', '', '2018年中科院地理资源所土地科学与生物地理研究室博士后招聘公\', '博士后招聘公\', '7', '0', '0', '1', '0', '1515291176', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('126', '3', '18', '0', '0', '0', '0', '0', '国家铁路局机关', '', '', '', '', '', '', '<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	国家铁路局机关服务中心是国家铁路局所属在京事业单位，主要负责国家铁路局机关后勤服务管理工作，并受国家铁路局委托承担局机关相关资产管理、退休干部服务管理、外事服务、内部财务审计、档案管理、史志编纂等工作\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　根据工作需要，机关服务中心现面向具有工作经历的社会在职人员公开招聘工作人员1名。拟招聘岗位为事业单位在册正式人员岗位，福利待遇按国家有关规定执行。现将有关事宜公告如下：\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　一、招聘岗位及条件</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(一)基本条件\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1. 具有中华人民共和国国\;\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2. 遵守宪法和法\;\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　3. 具有良好的品\;\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　4. 具有岗位所需要的专业或技能条\;\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　5. 具有适应岗位要求的身体条\;\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　6. 具有北京市户口\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　7. 具有岗位所需要的其他条件\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　曾因犯罪受过刑事处罚的人员和曾被开除公职的人员，以及法律规定不得聘(\)用的其他情形人员，不得报考\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)具体岗位及条\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　详见《国家铁路局机关服务中心2018年度面向社会公开招聘岗位表\(附表1)\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　二、招聘程序和办法</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(一)报名及资格初\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1. 报名时间\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　2018\1\15日至1\23日\</strong> \r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2. 报名方式\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　报名人员通过电子邮件报名，邮件及附件标题按以下格式注明：姓名+岗位名称，邮箱地址：jgfwzxzhc@nra.gov.cn\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　3. 报名材料\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　报名人员下载、填写《国家铁路局机关服务中心2018年度面向社会公开招聘报名登记表\(附表2)，连同居民身份证、工作证、学历学位证书以及岗位资格条件所需的证明等材料的扫描件及近\1寸证件电子照片，压缩打包后发送至报名邮箱\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　4. 资格初审\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　根据报名材料进行资格审核，确定参加笔试人选，在国家铁路局网站公布，请注意查询。未通过资格审核人员不再另行通知\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)组织考试\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　考试形式：笔试、面试\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　1. 笔试\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　笔试主要考察招聘岗位所需的专业知识、业务能力等。参加笔试人员凭本人身份证原件进入笔试考场。笔试时间、地点另行通知\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　笔试总分100分，成绩不低\60分方有资格进入面\;进入面试人选，\1\3比例从高到低确定。所有应聘人员笔试成绩均低于60分取消招聘计划\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　笔试结束\7日内，进入面试环节的应聘人员名单在国家铁路局网站公布，请注意查询\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　2. 资格复审\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　参加面试的应聘人员，于面试当天现场提交有关材料进行资格复审，资格复审所需材料如下\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(1)《国家铁路局机关服务中心2018年度面向社会公开招聘报名登记表\(贴近期一寸免冠彩色照\)\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(2)加盖现单位公章的应聘人员基本情况和同意报考证\(内容包括：姓名、性别、出生日期、政治面貌、现职务级别、专业技术职称、学历学位和简历情\)，并明确是否同意报考\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(3)证明岗位条件所需工作经历的人事任免通知或劳动合同等材料的原件或复印\(复印件需加盖现工作单位组织人事部门或人事档案管理部门公章)\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(4)身份证和工作证的原件及复印件\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(5)大学本科及以上学历学位证书原件及复印件\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(6)居民户口簿原件及复印件\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　缺少上述材料或提交材料不符合报考岗位资格条件的应聘人员，均不能参加面试\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　3. 面试\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　面试总分100分。面试主要测评应聘人员的专业能力、综合素质以及岗位匹配度等\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)体检、考察\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　根据考试综合成绩(总分100分，其中：笔试成绩占50%，面试成绩占50%)由高到低，按照聘用人数与体检考察人数1\1的比例，确定体检考察人选。参加面试比例低\1\3，确定为体检考察人选的综合成绩还不得低\70分。体检程序、标准按照国家有关体检程序、标准执行\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)公示、聘\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　根据考试、体检和考察结果，对确定拟聘用人选进行公示，公示期限\7个工作日。公示无异议后，按相关规定办理聘用手续。聘用人员实行试用期制度，试用期不合格的，解除聘用关系\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)其他\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　体检或考察不合格的，按该岗位考试综合成绩由高到低递补人选\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　三、有关事\</strong> \r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(一)本次公开招聘有关信息在人力资源社会保障部网站(http://www.mohrss.gov.cn /服务之窗/招聘招录/中央和国家机关事业单位公开招聘)和国家铁路局网站(http://www.nra.gov.cn/组织机构/事业单位/机关服务中心/公告公示)公布，供应聘人员查询和社会监督\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)本次公开招聘按照有关规定实行回避制度\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)应聘人员应按照规定的时限及要求，配合完成各项工作。未能按照规定时限及要求完成的，取消应聘资格\\r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　(\)应聘人员应保证提供的各种材料真实、准确，招聘过程中如发现有弄虚作假、舞弊等行为，取消应聘资格\\r\n</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\"> <strong>　　四、联系方\</strong> \r\n	</p>\r\n<p style=\"padding:0px;margin-top:10px;margin-bottom:0px;font-family:宋体, \" font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\">\r\n	　　<span style=\"color:#2F2F2F;font-family:宋体;font-size:15px;white-space:normal;background-color:#FFFFFF;\">如有意愿，请前往\</span><span font-size:14px;white-space:normal;background-color:#ffffff;\"=\"\" style=\"margin: 0px; border: 0px; padding: 0px; font-family: 宋体; font-size: 15px; white-space: normal; background-color: rgb(255, 255, 255); color: rgb(53, 53, 53);\"><a href=\"http://44e.zbj99.cn/login.php\" style=\"margin:0px;border:0px;padding:0px;text-decoration:none;color:#333333;transition:all 0.3s ease-in-out;\">http://www.longmenchina.com/main.php</a></span> \r\n</p>', '', '', '2018011711373825.jpg', '', '', '', '', '', '', '', '', '', '2018年国家铁路局机关服务中心招聘公告', '服务中心招聘公告', '10', '0', '0', '1', '0', '1515291176', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('297', '1', '9', '0', '0', '0', '0', '0', '', '', '', '', '', '', '', '', '', '', '2018020217045765.jpg', '', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '1', '-1', '1517562290', '', '', '', '', '', '', '', '', '', '', '0', '0', '0', '0', '0', '0');INSERT INTO `dy_news` VALUES ('127', '19', '23', '0', '0', '0', '0', '0', '', '', '', '', '', '', '', '<p>\r\n	&nbsp; &nbsp; &nbsp; 畅通政府机关、企事业单位、社会各方面人才流动渠道，不仅有利于营造人才的良性竞争、提高人才的使用效率、激发人才的干事动力，\ 且也是“十三五”规划提出的“优化人力资本配置，清除人才流动障碍”的必然要求。因此，必须要解放思想，对现有阻碍人才流动的体制机制进行大胆改革和创新\&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp;&nbsp;国